import crypto from "crypto";
import { CharacteristicGetCallback, uuid } from "hap-nodejs";
import { EventEmitter } from "events";
import { BridgeSetupManagerControlPoint, BridgeSetupManagerState } from "./bridgeSetupManager";
import {
  ActionType,
  InterfaceInstructionResponse,
  InterfaceListResponse,
  InterfaceRequest,
  InterfaceType,
  NegotiateResponse,
  PlatformContext,
  PluginResponse,
  Request,
  RequestType,
  Response,
  ResponseType,
} from "./bridgeSetupApi";
import { ConfigurablePlatformPlugin, PlatformIdentifier, PlatformName, PluginType } from "../api";
import { AccessoryConfig, HomebridgeConfig, PlatformConfig } from "../server";

enum SessionState {
    AWAIT_NEGOTIATION,
    AWAIT_SELECTION,
    LIST_PLATFORMS, // awaiting selection to give session to plugin
    FORWARD_MESSAGE_TO_PLATFORM,
    MANAGE_CONFIGURATION,
}

export enum BridgeSetupSessionEvent {
    NEW_CONFIG = "newConfig",
    REQUEST_CURRENT_CONFIG = "requestCurrentConfig",
    END = "end",
}

export type RequestCurrentConfigCallback = (config: HomebridgeConfig) => void;

export declare interface BridgeSetupSession {

    // "newConfig" for type Accessory is actually unsupported
    on(event: "newConfig", listener: (type: PluginType, name: PlatformName | PlatformIdentifier, replace: boolean, config: PlatformConfig | AccessoryConfig) => void): this;
    on(event: "requestCurrentConfig", listener: (callback: RequestCurrentConfigCallback) => void): this;
    on(event: "end", listener: () => void): this;

    emit(event: BridgeSetupSessionEvent.NEW_CONFIG, type: PluginType, name: PlatformName | PlatformIdentifier, replace: boolean, config: PlatformConfig | AccessoryConfig): boolean;
    emit(event: BridgeSetupSessionEvent.REQUEST_CURRENT_CONFIG, callback: RequestCurrentConfigCallback): boolean;
    emit(event: BridgeSetupSessionEvent.END): boolean;

}

export class BridgeSetupSession extends EventEmitter {

    private readonly configurablePlatformPlugins: Map<PlatformName | PlatformIdentifier, ConfigurablePlatformPlugin>;

    private readonly stateCharacteristic: BridgeSetupManagerState;
    private readonly controlCharacteristic: BridgeSetupManagerControlPoint;

    currentState = SessionState.AWAIT_NEGOTIATION;

    validSession = false;
    sessionUUID: string;

    transactionID = 0;
    preferredLanguage = "en-US";

    lastResponse = ""; // base64 encoded json payload

    listOfPlatforms?: PlatformName | PlatformIdentifier[]; // calculated list of plugin names sent to the opponent
    currentPluginName?: PlatformName | PlatformIdentifier;
    currentPlatformInstance?: ConfigurablePlatformPlugin;
    currentPlatformContext?: PlatformContext;

    constructor(configurablePlatformPlugins: Map<PlatformName | PlatformIdentifier, ConfigurablePlatformPlugin>, stateCharacteristic: BridgeSetupManagerState, controlCharacteristic: BridgeSetupManagerControlPoint) {
      super();
      this.configurablePlatformPlugins = configurablePlatformPlugins;
      this.stateCharacteristic = stateCharacteristic;
      this.controlCharacteristic = controlCharacteristic;
      this.sessionUUID = uuid.generate(crypto.randomBytes(32));
    }

    handleWriteRequest(request: Request): void {
      switch (request.type) {
        case RequestType.NEGOTIATE: {
          this.transactionID = request.tid + 1;
          this.preferredLanguage = request.language;

          this.validSession = true;

          this.currentState = SessionState.AWAIT_SELECTION;
          const response: NegotiateResponse = {
            tid: this.transactionID,
            type: ResponseType.NEGOTIATE,
            sid: this.sessionUUID,

            attachment: {
              type: ResponseType.INTERFACE,
              interface: InterfaceType.LIST,
              title: "How can I help you?",
              items: [
                ActionType.MANAGE_PLATFORM,
                ActionType.MANAGE_ACCESSORIES,
              ],
            },
          };


          this.sendResponse(response);
          break;
        }
        case RequestType.INTERFACE:
          this.transactionID = request.tid;

          if (this.currentState === SessionState.AWAIT_SELECTION) {
            const selectedIndex: number = request.response!.selections[0];
            let action: ActionType | undefined = undefined;

            if (selectedIndex === 0) {
              action = ActionType.MANAGE_PLATFORM;
            } else if (selectedIndex === 1) {
              action = ActionType.MANAGE_ACCESSORIES;
            }

            if (action === ActionType.MANAGE_PLATFORM) {
              this.presentManagePlatformMenu();
            } else if (action === ActionType.MANAGE_ACCESSORIES) {
              this.presentManageAccessoryMenu();
            }
          } else if (this.currentState === SessionState.LIST_PLATFORMS) {
            const selectedIndex = request.response!.selections[0];
            const targetPlatformName = this.listOfPlatforms![selectedIndex];
            const targetPlatform = this.configurablePlatformPlugins.get(targetPlatformName);

            // noinspection SpellCheckingInspection
            this.currentPlatformContext = {
              preferedLanguage: this.preferredLanguage,
            };
            this.currentPluginName = targetPlatformName;
            this.currentPlatformInstance = targetPlatform;

            this.currentState = SessionState.FORWARD_MESSAGE_TO_PLATFORM;

                    this.currentPlatformInstance!.configurationRequestHandler(this.currentPlatformContext, null, this.pluginResponseHandler.bind(this));
          } else if (this.currentState === SessionState.FORWARD_MESSAGE_TO_PLATFORM) {
                    this.currentPlatformInstance!.configurationRequestHandler(this.currentPlatformContext!, request, this.pluginResponseHandler.bind(this));
          } else if (this.currentState === SessionState.MANAGE_CONFIGURATION) {
            this.handleManageAccessory(request);
          }
          break;
        case RequestType.TERMINATE:
          this.transactionID = request.tid;
          this.validSession = false;

          if (this.currentState === SessionState.FORWARD_MESSAGE_TO_PLATFORM) {
                    this.currentPlatformInstance!.configurationRequestHandler(this.currentPlatformContext!, request, this.pluginResponseHandler.bind(this));
          }
          break;
      }
    }

    handleReadRequest(callback: CharacteristicGetCallback): void {
      callback(null, this.lastResponse);
    }

    private pluginResponseHandler(response?: PluginResponse, type?: PluginType, replace?: boolean, config?: PlatformConfig): void {
      if (config) {
        this.emit(BridgeSetupSessionEvent.NEW_CONFIG, type!, this.currentPluginName!, replace!, config);
        this.presentMainMenu();
      } else if (response) {
        this.transactionID += 1;

        response.tid = this.transactionID;
        response.sid = this.sessionUUID;

        this.sendResponse(response as Response);
      }
    }

    private presentMainMenu(): void {
      this.currentState = SessionState.AWAIT_SELECTION;
      this.transactionID += 1;

      const response: InterfaceListResponse<ActionType> = {
        tid: this.transactionID,
        sid: this.sessionUUID,
        type: ResponseType.INTERFACE,
        interface: InterfaceType.LIST,
        title: "How can I help you?",
        items: [
          ActionType.MANAGE_PLATFORM,
          ActionType.MANAGE_ACCESSORIES,
        ],
      };

      this.sendResponse(response);
    }

    private presentManagePlatformMenu(): void {
      this.listOfPlatforms = Object.keys(this.configurablePlatformPlugins);

      this.currentState = SessionState.LIST_PLATFORMS;
      this.transactionID += 1;

      const response: InterfaceListResponse<string> = {
        tid: this.transactionID,
        type: ResponseType.INTERFACE,
        sid: this.sessionUUID,
        interface: InterfaceType.LIST,
        title: "Which platform?",
        items: this.listOfPlatforms,
      };

      this.sendResponse(response);
    }

    private presentManageAccessoryMenu(): void {
      // noinspection JSUnusedLocalSymbols
      // eslint-disable-next-line @typescript-eslint/no-unused-vars
      this.emit(BridgeSetupSessionEvent.REQUEST_CURRENT_CONFIG, config => {
        // idk what implementation was intended here for accessories, but we should be careful what we publicise from the config
      });

      this.currentState = SessionState.MANAGE_CONFIGURATION;
      this.transactionID += 1;

      const response: InterfaceInstructionResponse = {
        tid: this.transactionID,
        type: ResponseType.INTERFACE,
        sid: this.sessionUUID,
        interface: InterfaceType.INSTRUCTION,
        title: "Not implemented",
        detail: "This function is not yet implemented.\nPlease manually edit config.json for now.",
        showNextButton: true,
        heroImage: "",
      };

      this.sendResponse(response);
    }

    // eslint-disable-next-line @typescript-eslint/no-unused-vars
    private handleManageAccessory(request: InterfaceRequest): void {
      this.presentMainMenu();
    }

    private sendResponse(response: Response): void {
      if (this.validSession) {
        const serializedResponse = JSON.stringify(response);
        const data = new Buffer(serializedResponse).toString("base64");

        this.lastResponse = data;
        this.controlCharacteristic.updateValue(data);
      }
    }

}
